INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1000026, '马龙·白兰度', '男', 'Marlon Brando', '马龙·白兰度', '1924-04-03', '美国,内布拉斯加州奥马哈', '白羊座', '演员 / 导演', '好莱坞传奇演员，凭《教父》《末代探戈》两度荣获奥斯卡最佳男主角，被誉为20世纪最伟大的演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1000035, '布拉德·彼特', '男', 'Brad Pitt', '布拉德·彼特', '1963-12-18', '美国,俄克拉荷马州沙尼', '射手座', '演员 / 制片人', '好莱坞超级巨星，凭《无耻混蛋》《无处可逃》等众多影片成为全球最具影响力的演员之一，并以《大短片》赢得奥斯卡最佳男配角。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1000060, '大卫·鲍伊', '男', 'David Bowie', '大卫·鲍伊', '1947-01-08', '英国,伦敦', '摩羯座', '音乐家 / 演员', '英国传奇摇滚音乐家，跨界参演多部电影，在《致命魔术》中饰演发明家尼古拉·特斯拉，是20世纪最具影响力的艺术家之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1000093, '阿尔·帕西诺', '男', 'Al Pacino', '阿尔·帕西诺', '1940-04-25', '美国,纽约', '金牛座', '演员 / 导演', '好莱坞传奇演员，凭《闻香识女人》获奥斯卡最佳男主角，以《教父》《疤面煞星》《热天午后》等经典角色享誉影坛。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1000099, '宋慧乔', '女', 'Hye-kyo Song', '宋慧乔', '1981-11-22', '韩国,大邱', '射手座', '演员', '韩国著名女演员，凭《浪漫满屋》《太阳的后裔》等韩剧成为亚洲顶级女星，主演《黑暗荣耀》在全球Netflix大受欢迎。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1000522, '弗朗西斯·福特·科波拉', '男', 'Francis Ford Coppola', '弗朗西斯·福特·科波拉', '1939-04-07', '美国,密歇根州底特律', '白羊座', '导演 / 编剧 / 制片人', '好莱坞殿堂级导演，以《教父》三部曲和《现代启示录》确立了在世界电影史上的不朽地位，五次荣获奥斯卡奖。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1000651, '詹姆斯·肯恩', '男', 'James Caan', '詹姆斯·肯恩', '1940-03-26', '美国,纽约', '白羊座', '演员', '好莱坞著名演员，以《教父》中的桑尼·科莱昂一角成名，同时也出演了《超脱》等多部影片，一生塑造了众多经典银幕形象。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1000657, '迈克尔·基顿', '男', 'Michael Keaton', '迈克尔·基顿', '1951-09-05', '美国,宾夕法尼亚州', '处女座', '演员', '好莱坞知名演员，早年以《蝙蝠侠》系列成名，近年凭借《鸟人》获奥斯卡提名，在《蜘蛛侠：英雄归来》饰演秃鹰。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1000785, '理查德·卡斯特尔诺', '男', 'Richard Castellano', '理查德·卡斯特尔诺', '1933-09-04', '美国,纽约', '处女座', '演员', '美国演员，以《教父》中饰演彼得·克莱门扎一角广为人知，获奥斯卡最佳男配角提名。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1000854, '关继威', '男', 'James Hong', '关继威', '1929-02-22', '美国,明尼苏达州明尼阿波利斯', '双鱼座', '演员', '美国华裔演员，演艺生涯长达七十年，出演过《瞬息全宇宙》等众多影片，是好莱坞历史上出镜率最高的华裔演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1002022, '安东尼奥·班德拉斯', '男', 'Antonio Banderas', '安东尼奥·班德拉斯', '1960-08-10', '西班牙,马拉加', '狮子座', '演员 / 导演', '西班牙著名演员，以《佐罗的面具》《西班牙公寓》等作品享誉全球，曾获戛纳最佳男主角奖。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1002072, '宋康昊', '男', 'Kang-ho Song', '宋康昊', '1967-01-17', '韩国,金海', '摩羯座', '演员', '韩国最著名的演员之一，代表作有《寄生虫》《雪国列车》《出租车司机》等，多次荣获韩国及国际电影奖项。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1002213, '杰米·李·柯蒂斯', '女', 'Jamie Lee Curtis', '杰米·李·柯蒂斯', '1958-11-22', '美国,加利福尼亚州洛杉矶', '射手座', '演员', '好莱坞著名女演员，凭《瞬息全宇宙》荣获奥斯卡最佳女配角，同时是《万圣节》系列的标志性主角，是好莱坞\"恐怖女皇\"之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1003162, '阿德里安·布洛迪', '男', 'Adrien Brody', '阿德里安·布洛迪', '1973-04-14', '美国,纽约', '白羊座', '演员', '美国著名演员，以《钢琴师》荣获奥斯卡最佳男主角，是史上最年轻的奥斯卡影帝，也出演了《布达佩斯大饭店》《超脱》等。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1003169, '迈克尔·凯恩', '男', 'Michael Caine', '迈克尔·凯恩', '1933-03-14', '英国,伦敦', '双鱼座', '演员', '英国传奇演员，出演超过100部电影，两度荣获奥斯卡最佳男配角，与导演克里斯托弗·诺兰多次合作。');
