INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1005614, '范伟', '男', 'Wei Fan', '范伟', '1962-05-22', '中国,辽宁沈阳', '双子座', '演员 / 喜剧演员', '中国著名演员和喜剧演员，以与赵本山合作的小品系列享誉全国，同时凭《不成问题的问题》等电影荣获国内外电影奖项。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1007110, '昆汀·塔伦蒂诺', '男', 'Quentin Tarantino', '昆汀·塔伦蒂诺', '1963-03-27', '美国,田纳西州诺克斯维尔', '白羊座', '导演 / 编剧 / 演员', '好莱坞独立导演代表，以独特的暴力美学和非线性叙事著称，代表作有《低俗小说》《无耻混蛋》《被解救的姜戈》等，两度荣获奥斯卡最佳原创剧本。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1007602, '肯尼斯·布拉纳', '男', 'Kenneth Branagh', '肯尼斯·布拉纳', '1960-12-10', '英国,北爱尔兰贝尔法斯特', '射手座', '演员 / 导演', '英国著名演员兼导演，以改编莎士比亚著称，出演《信条》《奥本海默》等，并自导自演《贝尔法斯特》获奥斯卡最佳原创剧本。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1007701, '克里斯·埃文斯', '男', 'Chris Evans', '克里斯·埃文斯', '1981-06-13', '美国,马萨诸塞州波士顿', '双子座', '演员 / 导演', '好莱坞著名演员，以饰演漫威电影中的史蒂夫·罗杰斯/美国队长而广受认可，是漫威电影宇宙的核心成员。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1007714, '陈冲', '女', 'Joan Chen', '陈冲', '1961-04-26', '中国,上海', '金牛座', '演员 / 导演', '中美两栖著名女演员，早年以《小花》成名，后在好莱坞主演《末代皇帝》《误杀》等，是最具国际影响力的华人女演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1009302, '神木隆之介', '男', 'Ryunosuke Kamiki', '神木隆之介', '1993-05-19', '日本,埼玉', '金牛座', '演员', '日本著名演员，童星出身，以《你的名字》《鬼灭之刃》等作品享誉日本，是日本90后最受欢迎的男演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1011473, '吴镇宇', '男', 'Francis Ng', '吴镇宇', '1961-04-04', '中国香港', '白羊座', '演员', '香港著名演员，以《古惑仔》系列《无间道》《默杀》等作品驰名两岸三地，是香港最具代表性的实力派男演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1013149, '马修·麦康纳赫', '男', 'Matthew McConaughey', '马修·麦康纳赫', '1969-11-04', '美国,德克萨斯州', '天蝎座', '演员 / 制片人', '美国著名演员，以《达拉斯买家俱乐部》荣获奥斯卡最佳男主角，代表作还有《星际穿越》《真探》等，是好莱坞最受认可的实力派男星之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1013157, '休·杰克曼', '男', 'Hugh Jackman', '休·杰克曼', '1968-10-12', '澳大利亚,悉尼', '天秤座', '演员 / 歌手', '澳大利亚著名演员，以《X战警》系列饰演金刚狼名扬天下，同时也是百老汇音乐剧明星，主演《澳洲乱世情》《致命魔术》等。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1014761, '汤姆·汉克斯', '男', 'Tom Hanks', '汤姆·汉克斯', '1956-07-09', '美国,加利福尼亚州康科德', '巨蟹座', '演员 / 导演 / 编剧', '好莱坞殿堂级演员，连续两届荣获奥斯卡最佳男主角（《费城故事》《阿甘正传》），代表作还有《芬奇》《荒岛余生》等，是美国最受尊敬的演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1017982, '克里斯蒂安·贝尔', '男', 'Christian Bale', '克里斯蒂安·贝尔', '1974-01-30', '英国,威尔士', '水瓶座', '演员', '英国著名演员，以《美国精神病人》《机械师》《蝙蝠侠三部曲》等作品闻名，曾获奥斯卡最佳男配角奖。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1019414, '安妮·海瑟薇', '女', 'Anne Hathaway', '安妮·海瑟薇', '1982-11-12', '美国,纽约', '天蝎座', '演员 / 歌手', '好莱坞著名女演员，以《悲惨世界》荣获奥斯卡最佳女配角，代表作还有《星际穿越》《穿普拉达的女王》等，是当代好莱坞最具影响力的女演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1019765, '马克·沃尔伯格', '男', 'Mark Wahlberg', '马克·沃尔伯格', '1971-06-05', '美国,马萨诸塞州波士顿', '双子座', '演员 / 制片人', '好莱坞知名演员和制片人，从说唱歌手转型成功，代表作有《泰迪熊》《变形金刚》《神秘海域》等。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1019766, '马特·达蒙', '男', 'Matt Damon', '马特·达蒙', '1970-10-08', '美国,马萨诸塞州剑桥', '天秤座', '演员 / 编剧 / 制片人', '好莱坞著名演员，以《心灵捕手》荣获奥斯卡最佳原创剧本，主演《拯救大兵瑞恩》《奥本海默》《火星救援》等众多经典，是好莱坞最重要的演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1019798, '詹妮弗·康纳利', '女', 'Jennifer Connelly', '詹妮弗·康纳利', '1970-12-12', '美国,纽约州卡茨基尔', '射手座', '演员', '好莱坞著名女演员，凭《美丽心灵》荣获奥斯卡最佳女配角，同时出演了《阿丽塔：战斗天使》《壮志凌云：独行侠》等。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1019977, '瑞恩·高斯林', '男', 'Ryan Gosling', '瑞恩·高斯林', '1980-11-12', '加拿大,伦敦', '天蝎座', '演员 / 音乐人', '加拿大著名演员，以《爱乐之城》《亡命驾驶》《芭比》等作品两度获奥斯卡最佳男主角提名，是好莱坞最具魅力的男星之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1020684, '玛丽莎·托梅', '女', 'Marisa Tomei', '玛丽莎·托梅', '1964-12-04', '美国,纽约布鲁克林', '射手座', '演员', '好莱坞女演员，凭《我的表兄文尼》获奥斯卡最佳女配角，在漫威系列中饰演梅姑。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1022060, '秦沛', '男', 'Paul Chun', '秦沛', '1947-03-27', '中国香港', '白羊座', '演员', '香港资深演员，出演过《误杀》等多部粤语及普通话影视作品，在影视界耕耘数十年。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1022234, '丹尼尔·克雷格', '男', 'Daniel Craig', '丹尼尔·克雷格', '1968-03-02', '英国,柴郡', '双鱼座', '演员', '英国著名演员，以第六任007詹姆斯·邦德闻名全球，同时也是《利刃出鞘》等多部非邦德影片的主演。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1022244, '舒淇', '女', 'Qi Shu', '舒淇', '1976-04-16', '中国台湾', '白羊座', '演员', '台湾著名女演员，以《千禧曼波》《刺客聂隐娘》《上海堡垒》等多部影片享誉两岸及国际影坛，是华语电影最重要的女演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1022870, '布莱恩·辛格', '男', 'Bryan Singer', '布莱恩·辛格', '1965-09-17', '美国,纽约', '处女座', '导演 / 编剧 / 制片人', '美国著名导演，执导了《X战警》系列多部、《波西米亚狂想曲》（中途离职）等大制作影片。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1022897, '吴刚', '男', 'Gang Wu', '吴刚', '1966-03-05', '中国,北京', '双鱼座', '演员', '中国著名演员，活跃于影视两栖，以饰演各类角色见长，代表作有《战狼2》《山海情》等。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1023048, '马克·里朗斯', '男', 'Mark Rylance', '马克·里朗斯', '1960-01-18', '英国,阿什福德', '摩羯座', '演员 / 剧作家', '英国著名舞台剧演员，凭《间谍之桥》荣获奥斯卡最佳男配角，与斯皮尔伯格多次合作。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1023439, '罗伯特·罗德里格斯', '男', 'Robert Rodriguez', '罗伯特·罗德里格斯', '1968-06-20', '美国,德克萨斯州圣安东尼奥', '双子座', '导演 / 编剧 / 制片人', '好莱坞独立导演，以超低成本拍摄《杀手无情》起家，后执导《墨西哥往事》《罪恶都市》《阿丽塔：战斗天使》等。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1023448, '约翰·雷吉扎莫', '男', 'John Leguizamo', '约翰·雷吉扎莫', '1964-07-22', '哥伦比亚,波哥大', '巨蟹座', '演员 / 喜剧演员 / 编剧', '哥伦比亚裔美国演员和喜剧演员，出演《爱乐之城》《超级马里奥》等多部影片。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1023632, '小罗伯特·唐尼', '男', 'Robert Downey Jr.', '罗伯特·唐尼', '1965-04-04', '美国,纽约', '白羊座', '演员 / 制片人', '好莱坞顶级演员，以饰演《钢铁侠》系列中的托尼·斯塔克/钢铁侠闻名全球，是漫威电影宇宙的核心人物，曾获奥斯卡最佳男配角奖。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1024624, '杨紫琼', '女', 'Michelle Yeoh', '杨紫琼', '1962-08-06', '马来西亚,怡保', '狮子座', '演员', '马来西亚华裔女演员，以《瞬息全宇宙》荣获奥斯卡最佳女主角，是首位荣获此奖的亚裔女演员，代表作还有《007之明日帝国》《卧虎藏龙》等。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1025159, '徐峥', '男', 'Xu Zheng', '徐峥', '1972-07-15', '中国,上海', '巨蟹座', '演员 / 导演', '中国著名演员兼导演，主导并主演《泰囧》《我不是药神》等多部现象级喜剧，屡创中国电影票房纪录。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1027360, '宁浩', '男', 'Ning Hao', '宁浩', '1977-12-09', '中国,山西太原', '射手座', '导演 / 编剧', '中国著名导演，凭借《疯狂的石头》《疯狂的赛车》等黑色幽默喜剧确立了独特风格，是中国商业电影的代表性导演。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1027415, '马克·鲁法洛', '男', 'Mark Ruffalo', '马克·鲁法洛', '1967-11-22', '美国,威斯康星州基诺沙', '射手座', '演员 / 制片人', '好莱坞著名演员，以饰演漫威电影中的布鲁斯·班纳/绿巨人而广为人知，曾多次获奥斯卡最佳男配角提名。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1027440, '刘德华', '男', 'Andy Lau', '刘德华', '1961-09-27', '中国香港', '天秤座', '演员 / 歌手 / 制片人', '香港殿堂级演员和歌手，以《流浪地球2》《无间道》《天若有情》等代表作享誉亚洲，是华语娱乐圈最具影响力的艺人之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1028064, '贾静雯', '女', 'Puff Kuo', '贾静雯', '1976-01-04', '中国台湾', '摩羯座', '演员', '台湾著名女演员，以《瀑布》中深具力量的母亲角色获得多项影后殊荣，代表了台湾电影最高水准之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1029381, 'J·K·西蒙斯', '男', 'J.K. Simmons', 'J·K·西蒙斯', '1955-01-09', '美国,密歇根州底特律', '摩羯座', '演员', '好莱坞著名配角演员，以《爆裂鼓手》荣获奥斯卡最佳男配角，也是《蜘蛛侠》系列中J·乔纳·贾姆森的扮演者。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1029652, '李雪健', '男', 'Xuejian Li', '李雪健', '1954-03-31', '中国,山东', '白羊座', '演员', '中国著名表演艺术家，以《宋江》《流浪地球2》等影视作品享誉全国，是中国文艺界最具代表性的老一辈演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1029664, '姜武', '男', 'Wu Jiang', '姜武', '1968-11-13', '中国,北京', '天蝎座', '演员', '中国著名演员，以《我的父亲母亲》《八佰》《老炮儿》等多部影片展现出色演技，是中国最受尊敬的实力派男演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1029742, '树木希林', '女', 'Kiki Kirin', '树木希林', '1943-01-15', '日本,东京', '摩羯座', '演员', '日本著名女演员，出演了是枝裕和多部作品，以细腻、自然的表演风格著称，代表作有《小偷家族》《比海更深》等。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1029890, '吴孟达', '男', 'Man Tat Ng', '吴孟达', '1952-01-08', '中国,福建厦门', '摩羯座', '演员', '香港著名演员，以与周星驰搭档的系列电影享誉华语影坛，晚年主演《流浪地球》，2021年因病辞世。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1032657, '马克·斯特朗', '男', 'Mark Strong', '马克·斯特朗', '1963-08-05', '英国,伦敦', '狮子座', '演员', '英国著名演员，以饰演各类反派或严肃配角著称，出演过《1917》《金牌特务》《绿灯侠》等众多影片。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1033672, '周一围', '男', 'Yiwei Zhou', '周一围', '1982-03-10', '中国,湖南', '双鱼座', '演员', '中国演员，以《我不是药神》等影视作品走红，在影坛积累了较高的口碑和认可度。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1033680, '是枝裕和', '男', 'Hirokazu Koreeda', '是枝裕和', '1962-06-06', '日本,东京', '双子座', '导演 / 编剧', '日本著名导演，以《无人知晓》《小偷家族》等作品享誉世界，《小偷家族》荣获戛纳电影节金棕榈奖，是当代日本最重要的导演之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1033698, '萨日娜', '女', 'Sarina', '萨日娜', '1968-01-22', '中国,内蒙古', '摩羯座', '演员', '中国蒙古族著名女演员，以《母亲》《抓娃娃》等作品享有盛誉，是中国影视界最受尊重的母亲形象代言人之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1034553, '维果·莫腾森', '男', 'Viggo Mortensen', '维果·莫腾森', '1958-10-20', '美国,纽约', '天秤座', '演员 / 诗人 / 摄影师', '美国著名演员，以《指环王》三部曲中的阿拉贡成名，凭《绿皮书》《好人》等三度获奥斯卡提名，是好莱坞最具文艺气质的演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1035614, '朱媛媛', '女', 'Yuanyuan Zhu', '朱媛媛', '1977-09-09', '中国,辽宁', '处女座', '演员', '中国著名女演员，以《我的姐姐》《父母爱情》等作品知名，擅长塑造质朴真实的平凡女性形象。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1036302, '杰森·贝特曼', '男', 'Jason Bateman', '杰森·贝特曼', '1969-01-14', '美国,纽约州罗切斯特', '摩羯座', '演员 / 导演', '美国演员和导演，以《发展受阻》《无耻之徒》及《疯狂动物城》中尼克配音广为人知，风格幽默犀利。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1037241, '露西·刘', '女', 'Lucy Liu', '露西·刘', '1968-12-02', '美国,纽约', '射手座', '演员 / 导演', '美国华裔女演员，以《霹雳娇娃》《盲探》等作品知名，是好莱坞最著名的华裔演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1037920, '黄渤', '男', 'Huang Bo', '黄渤', '1974-08-26', '中国,山东青岛', '处女座', '演员 / 导演', '中国著名演员，以《疯狂的石头》《斗牛》等作品赢得大量赞誉，被誉为中国最具票房号召力的男演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1038601, '迈克尔·法斯宾德', '男', 'Michael Fassbender', '迈克尔·法斯宾德', '1977-04-02', '德国,海德堡', '白羊座', '演员 / 制片人', '德裔爱尔兰演员，以《羞耻》《12年为奴》等作品两度获奥斯卡提名，在《X战警》系列中饰演万磁王。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1039218, '沙溢', '男', 'Sha Yi', '沙溢', '1975-02-28', '中国,新疆乌鲁木齐', '双鱼座', '演员', '中国著名演员，活跃于影视两栖，代表作有《武林外传》《流浪地球2》等，以幽默风格深受观众喜爱。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1039222, '克里斯·海姆斯沃斯', '男', 'Chris Hemsworth', '克里斯·海姆斯沃斯', '1983-08-11', '澳大利亚,墨尔本', '狮子座', '演员', '澳大利亚演员，以饰演漫威电影《雷神》系列中的托尔/雷神而享誉国际，是好莱坞最受欢迎的动作片明星之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1040173, '莱恩·约翰逊', '男', 'Rian Johnson', '莱恩·约翰逊', '1973-12-17', '美国,马里兰州', '射手座', '导演 / 编剧', '美国导演，以《利刃出鞘》系列确立了其在悬疑喜剧领域的地位，同时也是《星球大战：最后的绝地武士》的导演。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1041483, '克里斯托弗·瓦尔兹', '男', 'Christoph Waltz', '克里斯托弗·瓦尔兹', '1956-10-04', '奥地利,维也纳', '天秤座', '演员', '奥地利著名演员，凭借《无耻混蛋》和《被解救的姜戈》两度荣获奥斯卡最佳男配角，是好莱坞最具魅力的反派演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1043800, '钟汉良', '男', 'Wallace Chung', '钟汉良', '1974-08-22', '中国香港', '狮子座', '演员 / 歌手', '香港演员和歌手，两栖发展，出演《上海堡垒》《古剑奇谭》等多部影视，在大陆和香港都有大批粉丝。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1044093, '黛安·克鲁格', '女', 'Diane Kruger', '黛安·克鲁格', '1976-07-15', '德国,阿尔费尔德', '巨蟹座', '演员', '德国女演员，以《无耻混蛋》中的女间谍和《黑皮书》等欧洲影片闻名，曾获戛纳最佳女主角奖。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1045752, '黄觉', '男', 'Jue Huang', '黄觉', '1976-09-03', '中国,湖南', '处女座', '演员', '中国著名演员，以《立春》《少年的你》《地久天长》等文艺片享有声誉，是中国最受认可的实力派男演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1046699, '伊卓瑞斯·艾尔巴', '男', 'Idris Elba', '伊卓瑞斯·艾尔巴', '1972-09-06', '英国,伦敦', '处女座', '演员 / 导演 / 音乐人', '英国著名演员，以《路德》《雷神》《疯狂动物城》等作品享誉全球，曾多次被提名金球奖。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1046987, '杨皓宇', '男', 'Haoyu Yang', '杨皓宇', '1978-01-01', '中国', '摩羯座', '演员', '中国著名演员，以《宇宙探索编辑部》中的主角一举成名，是中国文艺电影圈颇受认可的实力派男演员。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1047476, '本·门德尔森', '男', 'Ben Mendelsohn', '本·门德尔森', '1969-08-03', '澳大利亚,墨尔本', '狮子座', '演员', '澳大利亚演员，以出演《头号玩家》《蝙蝠侠：黑暗骑士崛起》《侠盗一号》等大片中的反派而知名。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1049300, '弗兰克·格里罗', '男', 'Frank Grillo', '弗兰克·格里罗', '1965-06-08', '美国,纽约', '双子座', '演员', '美国动作片演员，以出演《美国队长》系列中的十字骨和《战狼2》等动作片著称，是好莱坞知名硬汉演员。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1049534, '艾米莉·布朗特', '女', 'Emily Blunt', '艾米莉·布朗特', '1983-02-23', '英国,伦敦', '双鱼座', '演员', '英国著名女演员，以《奥本海默》《边境杀手》《复仇8英里》等作品屡获国际大奖，是好莱坞最受推崇的实力派女演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1050523, '琳达·卡德里尼', '女', 'Linda Cardellini', '琳达·卡德里尼', '1975-06-25', '美国,加利福尼亚州', '巨蟹座', '演员', '美国女演员，以《绿皮书》和《复仇者联盟》系列知名，是好莱坞颇受认可的实力派中生代女演员。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1052297, '西岛秀俊', '男', 'Hidetoshi Nishijima', '西岛秀俊', '1970-06-29', '日本,神奈川', '巨蟹座', '演员', '日本著名演员，以《驾驶我的车》获得广泛国际关注，同时也是多部日本影视作品的主演，是日本最具代表性的实力派男演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1052363, '中川雅也', '男', 'Masahiro Higashide', '中川雅也', '1976-11-08', '日本,大阪', '天蝎座', '演员', '日本著名演员，出演过《小偷家族》等是枝裕和的多部作品，是日本中生代最受信赖的演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1054390, '阿米尔·汗', '男', 'Aamir Khan', '阿米尔·汗', '1965-03-14', '印度,孟买', '双鱼座', '演员 / 导演 / 制片人', '印度宝莱坞顶级明星，以《三傻大闹宝莱坞》《摔跤吧！爸爸》《未知死亡》等享誉全球，是印度电影最具代表性的人物之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1054432, '金妮弗·古德温', '女', 'Ginnifer Goodwin', '金妮弗·古德温', '1978-05-22', '美国,田纳西州纳什维尔', '双子座', '演员', '美国女演员，以电视剧《童话镇》和迪士尼动画《疯狂动物城》中朱迪的配音而著称。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1054450, '史蒂文·斯皮尔伯格', '男', 'Steven Spielberg', '史蒂文·斯皮尔伯格', '1946-12-18', '美国,俄亥俄州辛辛那提', '射手座', '导演 / 编剧 / 制片人', '好莱坞殿堂级导演，执导了《大白鲨》《ET外星人》《辛德勒的名单》《拯救大兵瑞恩》《头号玩家》等多部经典，三次荣获奥斯卡奖。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1054497, '托尼·凯耶', '男', 'Tony Kaye', '托尼·凯耶', '1952-05-08', '英国,伦敦', '金牛座', '导演 / 摄影师', '英国导演，以《美国X档案》《超脱》等社会题材电影著称，以独特的视觉风格和对人性黑暗面的深刻探索见长。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1054524, '克里斯托弗·诺兰', '男', 'Christopher Nolan', '克里斯托弗·诺兰', '1970-07-30', '英国,伦敦', '狮子座', '导演 / 编剧 / 制片人', '英国著名导演，代表作包括《盗梦空间》《星际穿越》《信条》《奥本海默》等，以烧脑叙事和宏大视觉著称，荣获奥斯卡最佳导演奖。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1054527, '萨姆·门德斯', '男', 'Sam Mendes', '萨姆·门德斯', '1965-08-01', '英国,雷丁', '狮子座', '导演 / 编剧 / 制片人', '英国著名导演，以《美国丽人》获奥斯卡最佳导演，后执导007系列《天幕危机》《幽灵党》及一战史诗《1917》。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1055302, '梅西·格雷', '女', 'Macy Gray', '梅西·格雷', '1969-09-06', '美国,俄亥俄州坎顿', '处女座', '歌手 / 演员', '美国著名R&B歌手，以单曲《I Try》获格莱美奖，同时也是出色的演员，参演过《蜘蛛侠》《超脱》等影片。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1055487, '罗伯特·帕丁森', '男', 'Robert Pattinson', '罗伯特·帕丁森', '1986-05-13', '英国,伦敦', '金牛座', '演员 / 音乐人', '英国演员，以《暮光之城》成名后不断转型，出演诺兰《信条》《蝙蝠侠》等，是好莱坞最具实力的新生代男演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1057638, '陈以文', '男', 'Yiwen Chen', '陈以文', '1968-12-01', '中国台湾', '射手座', '演员', '台湾著名演员，以《无声》《瀑布》《周处除三害》等多部作品获得国际电影节认可。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1060204, '彼得·法雷利', '男', 'Peter Farrelly', '彼得·法雷利', '1956-12-17', '美国,罗德岛州', '射手座', '导演 / 编剧', '美国导演，以《阿呆与阿瓜》系列等喜剧出名，后转型执导《绿皮书》，荣获奥斯卡最佳影片和最佳原创剧本奖。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1060841, '囧森瑟夫', '男', 'Joseph Gordon-Levitt', '约瑟夫·高登-莱维特', '1981-02-17', '美国,加利福尼亚州洛杉矶', '水瓶座', '演员 / 导演', '美国演员，以《盗梦空间》《哪吒之魔童降世》（哪吒配音者非本人，此处为误称）等闻名，是好莱坞文艺气质与商业价值兼具的演员。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (1062462, '宋佳', '女', 'Jia Song', '宋佳', '1981-07-12', '中国,黑龙江哈尔滨', '巨蟹座', '演员', '中国著名女演员，以《好东西》《扫黑·决战》等作品展现出色演技，是中国影坛最受好评的实力派女演员之一。');
