INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000007, '曾国祥', '男', 'Derek Tsang', '曾国祥', '1983-02-18', '中国,香港', '水瓶座', '导演', '香港导演，曾秀华之子，执导《少年的你》《七月与安生》，擅长青春情感题材。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000008, '文牧野', '男', 'Muye Wen', '文牧野', '1985-07-18', '中国,新疆', '巨蟹座', '导演', '中国导演，代表作《我不是药神》，该片获无数奖项并引发社会广泛讨论，是中国现实主义电影的标杆之作。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000009, '宁浩', '男', 'Hao Ning', '宁浩', '1977-11-22', '中国,山西', '射手座', '导演 / 编剧', '中国著名导演，\"坏猴子\"电影公司创始人，以\"疯狂\"系列多线叙事著称，代表作《疯狂的石头》《疯狂的外星人》。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000010, '管虎', '男', 'Hu Guan', '管虎', '1968-11-22', '中国,北京', '射手座', '导演', '中国著名导演，代表作《八佰》《老炮儿》，擅长战争题材和现实题材，风格粗粝有力。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000011, '饺子', '男', NULL, '杨宇', '1981-08-23', '中国,四川', '处女座', '导演 / 编剧 / 动画师', '本名杨宇，独立完成动画电影《哪吒之魔童降世》的主要工作，成为中国最受关注的动画导演。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000012, '谢君伟', '男', NULL, '谢君伟', '1985-03-15', '中国,广东', '双鱼座', '导演', '追光动画导演，代表作《长安三万里》，以中国历史和唐诗文化为素材，打造了高口碑的国产动画大片。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000013, '奉俊昊', '男', 'Bong Joon-ho', NULL, '1969-09-14', '韩国,大邱', '处女座', '导演 / 编剧', '韩国著名导演，凭《寄生虫》首次斩获戛纳金棕榈和奥斯卡最佳影片，是韩国电影走向世界的重要代表。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000014, '宋康昊', '男', 'Kang-ho Song', NULL, '1967-01-17', '韩国,釜山', '摩羯座', '演员', '韩国最具代表性的男演员之一，被誉为\"韩国的马龙·白兰度\"，代表作《寄生虫》《醉乡民谣》《杀人回忆》。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000015, '崔宇植', '男', 'Woo-sik Choi', NULL, '1990-03-26', '韩国,光州', '白羊座', '演员', '韩国演员，凭《寄生虫》引起广泛关注，后主演奈飞剧集《僵尸校园》进一步提升国际知名度。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000016, '宋慧乔', '女', 'Hye-kyo Song', NULL, '1981-11-22', '韩国,大邱', '射手座', '演员', '韩国著名女演员，主演《黑暗荣耀》引发全球追剧热潮，是韩流文化的标志性人物之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000017, '新海诚', '男', 'Makoto Shinkai', NULL, '1973-02-09', '日本,长野', '水瓶座', '导演 / 编剧', '日本著名动画导演，以唯美画面和细腻情感著称，代表作《你的名字》《天气之子》《铃芽之旅》。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000018, '是枝裕和', '男', 'Hirokazu Kore-eda', NULL, '1962-06-06', '日本,东京', '双子座', '导演 / 编剧', '日本著名导演，以家庭题材见长，代表作《小偷家族》荣获戛纳金棕榈奖，《无人知晓》《步履不停》等均为佳作。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000019, '滨口龙介', '男', 'Ryusuke Hamaguchi', NULL, '1978-12-16', '日本,神奈川', '射手座', '导演 / 编剧', '日本导演，凭《驾驶我的车》荣获奥斯卡最佳国际影片，是当代日本电影最重要的创作者之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000020, '山田尚子', '女', 'Naoko Yamada', NULL, '1981-11-28', '日本,京都', '射手座', '导演', '京都动画旗下导演，以《声之形》和K-ON!系列动画为代表，擅长细腻的人物情感描写。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000021, '克里斯托弗·诺兰', '男', 'Christopher Nolan', NULL, '1970-07-30', '英国,伦敦', '狮子座', '导演 / 编剧 / 制片人', '英美双国籍导演，以《盗梦空间》《星际穿越》《奥本海默》等作品闻名于世，凭《奥本海默》荣获奥斯卡最佳导演。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000022, '基里安·墨菲', '男', 'Cillian Murphy', NULL, '1976-05-25', '爱尔兰,科克', '双子座', '演员', '爱尔兰演员，凭《奥本海默》荣获奥斯卡最佳男主角，剧集《浴血黑帮》中的汤米·谢尔比使其举世闻名。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000023, '瑞恩·高斯林', '男', 'Ryan Gosling', NULL, '1980-11-12', '加拿大,安大略', '天蝎座', '演员', '加拿大演员，凭《爱乐之城》提名奥斯卡，在《芭比》中饰演肯大受好评，是好莱坞最具魅力的演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000024, '艾玛·斯通', '女', 'Emma Stone', NULL, '1988-11-06', '美国,亚利桑那', '天蝎座', '演员', '美国演员，两度荣获奥斯卡最佳女主角（《爱乐之城》和《可怜的东西》），是好莱坞当代最重要的女演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000025, '马赫沙拉·阿里', '男', 'Mahershala Ali', NULL, '1974-02-16', '美国,加利福尼亚', '水瓶座', '演员', '美国演员，两度荣获奥斯卡最佳男配角（《月光男孩》和《绿皮书》），是好莱坞最受尊敬的演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000026, '玛格特·罗比', '女', 'Margot Robbie', NULL, '1990-07-02', '澳大利亚,昆士兰', '巨蟹座', '演员 / 制片人', '澳大利亚演员，主演《芭比》《我，花样女王》，是好莱坞炙手可热的新生代女星，也是出色的制片人。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000027, '格蕾塔·葛韦格', '女', 'Greta Gerwig', NULL, '1983-08-04', '美国,加利福尼亚', '狮子座', '导演 / 编剧 / 演员', '美国导演，执导《伯德小姐》《小妇人》《芭比》，是好莱坞最具影响力的女性导演之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000028, '达米安·沙泽勒', '男', 'Damien Chazelle', NULL, '1985-01-19', '美国,新泽西', '摩羯座', '导演 / 编剧', '美国导演，凭《爱乐之城》成为奥斯卡史上最年轻的最佳导演得主，代表作还有《爆裂鼓手》《巴比伦》。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000029, '阿米尔·汗', '男', 'Aamir Khan', NULL, '1965-03-14', '印度,孟买', '双鱼座', '演员 / 导演 / 制片人', '印度宝莱坞最具影响力的演员之一，以严肃的选片态度著称，代表作《摔跤吧！爸爸》《三傻大闹宝莱坞》。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000030, '奥里奥尔·保罗', '男', 'Oriol Paulo', NULL, '1975-07-04', '西班牙,巴塞罗那', '巨蟹座', '导演 / 编剧', '西班牙导演，以悬疑惊悚片《看不见的客人》和《无名女尸》闻名，是欧洲最擅长反转剧情的导演之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000031, '徐峥', '男', 'Zheng Xu', '徐峥', '1972-04-18', '中国,上海', '白羊座', '演员 / 导演 / 编剧', '中国著名演员兼导演，主演《我不是药神》获金鸡、百花等奖项，执导《泰囧》打破国产片票房纪录。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000032, '王传君', '男', 'Chuanjun Wang', '王传君', '1987-05-28', '中国,上海', '双子座', '演员', '中国演员，凭《我不是药神》中的程勇老搭档吕受益一角深入人心，是中国最有潜力的实力派演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000033, '章宇', '男', 'Zhang Yu', '章宇', '1986-12-14', '中国,贵州', '射手座', '演员', '中国实力派演员，凭《我不是药神》和《无名》展现精湛演技，近年来备受业界和观众认可。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000034, '谭卓', '女', 'Zhuo Tan', '谭卓', '1984-09-02', '中国,黑龙江', '处女座', '演员', '中国演员，以独特的气质和扎实的演技见长，代表作《我不是药神》《烈日灼心》，是中国优秀的女演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000035, '朱一龙', '男', 'Yilong Zhu', '朱一龙', '1988-04-07', '中国,武汉', '白羊座', '演员', '中国演员，凭《消失的她》《人生大事》进入主流院线并赢得口碑，展现了多元的演技。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000036, '倪妮', '女', 'Ni Ni', '倪妮', '1988-10-08', '中国,南京', '天秤座', '演员', '中国著名女演员，以清冷气质著称，代表作《金陵十三钗》《消失的她》《宸汐缘》，屡获重要奖项提名。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000037, '宋佳', '女', 'Jia Song', '宋佳', '1982-01-08', '中国,黑龙江', '摩羯座', '演员', '中国著名女演员，塑造过众多个性鲜明的女性角色，代表作《好东西》《找到你》，多次获得金马奖提名。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000038, '张子枫', '女', 'Zifeng Zhang', '张子枫', '2001-09-10', '中国,安徽', '处女座', '演员', '中国新生代演员，童星出道，凭《我的姐姐》和《第一次的离别》多次斩获各大奖项最佳女主角。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000039, '肖央', '男', 'Yang Xiao', '肖央', '1980-09-10', '中国,河南', '处女座', '演员 / 导演 / 歌手', '中国全能艺人，以《筷子兄弟》组合出道，后转型为实力演员，代表作《误杀》《我的姐姐》《天才捕手》。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000040, '阮经天', '男', 'Mark Chao', '阮经天', '1984-09-07', '中国,台湾', '处女座', '演员', '台湾著名男演员，凭《周处除三害》中的陈桂林一角获得广泛赞誉，是华语影坛最具突破精神的演员之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000041, '邵艺辉', '女', NULL, '邵艺辉', '1990-06-15', '中国,山西', '双子座', '导演 / 编剧', '中国新生代女性导演，代表作《爱情神话》和《好东西》，擅长刻画都市女性生活，口碑极佳。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000042, '孔大山', '男', NULL, '孔大山', '1989-08-21', '中国,山东', '狮子座', '导演 / 编剧', '中国独立电影导演，凭《宇宙探索编辑部》获得极高口碑，是中国新生代最具想象力的导演之一。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000043, '申奥', '男', NULL, '申奥', '1988-03-30', '中国,北京', '白羊座', '导演 / 编剧', '中国导演，凭短片《法制未来时》崭露头角，长片处女作《引爆者》获好评，代表作《兔子暴力》展现独特风格。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000044, '柯汶利', '男', NULL, '柯汶利', '1990-05-12', '马来西亚,吉隆坡', '金牛座', '导演', '马来西亚导演，执导《误杀》和《默杀》，以精准的类型片操控力和高度紧张的叙事节奏见长，是华语商业片的新锐力量。');
INSERT INTO `movie_worker` (`worker_id`, `worker_name`, `gender`, `name_en`, `name_zh`, `birth`, `birthplace`, `constellatory`, `profession`, `biography`) VALUES (2000045, '黄精甫', '男', 'Wan-Cheung Wong', NULL, '1973-11-09', '中国,香港', '天蝎座', '导演 / 编剧', '香港导演，以暴力美学著称，代表作《复仇者之死》，执导台湾制作《周处除三害》赢得极高口碑。');
